# Advanced Banking System with Custom Exceptions

class InsufficientFundsError(Exception):
    """Custom exception for insufficient funds."""
    def __init__(self, balance, amount):
        self.balance = balance
        self.amount = amount
        super().__init__(f"Insufficient funds: ${balance:.2f} available, ${amount:.2f} requested")

class InvalidAccountError(Exception):
    """Custom exception for invalid account operations."""
    pass

class BankAccount:
    """A bank account with comprehensive error handling."""

    def __init__(self, account_number, initial_balance=0):
        if not isinstance(account_number, str) or len(account_number) != 10:
            raise ValueError("Account number must be a 10-character string")
        if initial_balance < 0:
            raise ValueError("Initial balance cannot be negative")

        self.account_number = account_number
        self.balance = float(initial_balance)
        self.transaction_history = []
        self.is_active = True

    def deposit(self, amount):
        """Deposit money with validation."""
        try:
            if not self.is_active:
                raise InvalidAccountError("Account is closed")

            amount = float(amount)
            if amount <= 0:
                raise ValueError("Deposit amount must be positive")
            if amount > 10000:
                raise ValueError("Single deposit cannot exceed $10,000")

            self.balance += amount
            self.transaction_history.append(("DEPOSIT", amount, self.balance))
            return f"âœ… Deposited ${amount:.2f}. New balance: ${self.balance:.2f}"

        except ValueError as e:
            return f"âŒ Deposit failed: {e}"
        except InvalidAccountError as e:
            return f"âŒ Account error: {e}"
        except Exception as e:
            return f"âŒ Unexpected error during deposit: {e}"

    def withdraw(self, amount):
        """Withdraw money with comprehensive validation."""
        try:
            if not self.is_active:
                raise InvalidAccountError("Account is closed")

            amount = float(amount)
            if amount <= 0:
                raise ValueError("Withdrawal amount must be positive")
            if amount > self.balance:
                raise InsufficientFundsError(self.balance, amount)
            if amount > 5000:
                raise ValueError("Single withdrawal cannot exceed $5,000")

            self.balance -= amount
            self.transaction_history.append(("WITHDRAWAL", amount, self.balance))
            return f"âœ… Withdrew ${amount:.2f}. New balance: ${self.balance:.2f}"

        except InsufficientFundsError as e:
            return f"âŒ {e}"
        except ValueError as e:
            return f"âŒ Withdrawal failed: {e}"
        except InvalidAccountError as e:
            return f"âŒ Account error: {e}"
        except Exception as e:
            return f"âŒ Unexpected error during withdrawal: {e}"

    def transfer(self, target_account, amount):
        """Transfer money between accounts."""
        try:
            if not isinstance(target_account, BankAccount):
                raise TypeError("Target must be a BankAccount object")
            if target_account.account_number == self.account_number:
                raise ValueError("Cannot transfer to the same account")

            # Attempt withdrawal first
            withdrawal_result = self.withdraw(amount)
            if "âŒ" in withdrawal_result:
                return f"Transfer failed: {withdrawal_result}"

            # Attempt deposit to target
            deposit_result = target_account.deposit(amount)
            if "âŒ" in deposit_result:
                # Rollback withdrawal
                self.balance += float(amount)
                self.transaction_history.append(("ROLLBACK", amount, self.balance))
                return f"Transfer failed during deposit: {deposit_result}"

            self.transaction_history.append(("TRANSFER_OUT", amount, self.balance))
            target_account.transaction_history.append(("TRANSFER_IN", amount, target_account.balance))

            return f"âœ… Transferred ${amount:.2f} to account {target_account.account_number}"

        except (TypeError, ValueError) as e:
            return f"âŒ Transfer failed: {e}"
        except Exception as e:
            return f"âŒ Unexpected error during transfer: {e}"

    def get_statement(self):
        """Generate account statement with error handling."""
        try:
            if not self.transaction_history:
                return "No transactions found."

            statement = f"\nğŸ’³ Account Statement - {self.account_number}\n"
            statement += "=" * 50 + "\n"
            statement += f"Current Balance: ${self.balance:.2f}\n"
            statement += f"Account Status: {'Active' if self.is_active else 'Closed'}\n\n"

            statement += "Recent Transactions:\n"
            for i, (trans_type, amount, balance) in enumerate(self.transaction_history[-5:], 1):
                statement += f"{i}. {trans_type}: ${amount:.2f} (Balance: ${balance:.2f})\n"

            return statement

        except Exception as e:
            return f"âŒ Error generating statement: {e}"

