    """Safe calculator with comprehensive error handling."""
    try:
        if operation == "divide":
            if b is None:
                return "âŒ Error: Division requires two numbers"
            if not isinstance(a, (int, float)) or not isinstance(b, (int, float)):
                raise TypeError("Both operands must be numbers")
            if b == 0:
                raise ZeroDivisionError("Cannot divide by zero")
            return f"{a} Ã· {b} = {a / b}"

        elif operation == "sqrt":
            if not isinstance(a, (int, float)):
                raise TypeError("Square root requires a number")
            if a < 0:
                raise ValueError("Cannot calculate square root of negative number")
            result = a ** 0.5
            return f"âˆš{a} = {result}"

        else:
            return f"âŒ Error: Unknown operation '{operation}'"

    except ZeroDivisionError as e:
        return f"âŒ Division Error: {e}"
    except ValueError as e:
        return f"âŒ Value Error: {e}"
    except TypeError as e:
        return f"âŒ Type Error: {e}"
