def process_users(users_list):
    """Process user data with comprehensive error handling."""
    try:
        # Validate input
        if not isinstance(users_list, list):
            raise TypeError("Input must be a list")

        if not users_list:
            return {"error": "Empty user list provided"}

        valid_users = []
        valid_ages = []
        valid_emails = 0
        processing_errors = []

        # Process each user
        for i, user in enumerate(users_list):
            try:
                if not isinstance(user, dict):
                    raise TypeError(f"User {i} is not a dictionary")

                # Process name
                name = user.get("name", f"Unknown_{i}")
                if not isinstance(name, str):
                    name = f"User_{i}"

                # Process age
                age = None
                if "age" in user:
                    try:
                        age = int(user["age"])
                        if age < 0 or age > 150:
                            raise ValueError(f"Invalid age: {age}")
                        valid_ages.append(age)
                    except (ValueError, TypeError):
                        processing_errors.append(f"User {i} ({name}): Invalid age '{user['age']}'")

                # Process email
                email = user.get("email", "")
                if isinstance(email, str) and "@" in email and "." in email:
                    valid_emails += 1
                elif email:  # Non-empty but invalid
                    processing_errors.append(f"User {i} ({name}): Invalid email '{email}'")

                # Store valid user info
                valid_users.append({
                    "name": name,
                    "age": age,
                    "email": email,
                    "index": i
                })

            except Exception as e:
                processing_errors.append(f"Error processing user {i}: {e}")
                continue

        # Calculate statistics
        summary = {
            "total_users": len(users_list),
            "processed_users": len(valid_users),
            "valid_emails": valid_emails,
            "processing_errors": processing_errors
        }

        # Average age
        if valid_ages:
            summary["average_age"] = round(sum(valid_ages) / len(valid_ages), 1)
            summary["users_with_age"] = len(valid_ages)
        else:
            summary["average_age"] = None
            summary["users_with_age"] = 0

        # Oldest user
        if valid_ages:
            oldest_age = max(valid_ages)
            oldest_users = [u for u in valid_users if u["age"] == oldest_age]
            summary["oldest_user"] = {
                "name": oldest_users[0]["name"],
                "age": oldest_age
            }
        else:
            summary["oldest_user"] = None

        return summary

    except TypeError as e:
        return {"error": f"Type Error: {e}"}
    except Exception as e:
        return {"error": f"Unexpected error: {e}"}

# Test the data processor
