# Library Management System with Functions

def create_book(title, author, year, isbn, genre="General"):
    """Create a book dictionary with validation."""
    if not title or not author:
        return None

    return {
        "title": title.title(),
        "author": author.title(),
        "year": year,
        "isbn": isbn,
        "genre": genre.title(),
        "borrowed": False,
        "borrower": None,
        "times_borrowed": 0
    }

def add_book_to_library(library, book):
    """Add a book to the library catalog."""
    if book and book["isbn"] not in library:
        library[book["isbn"]] = book
        return True
    return False

def search_books(library, search_term, search_type="title"):
    """Search for books by title, author, or genre."""
    search_term = search_term.lower()
    results = []

    for isbn, book in library.items():
        if search_type == "title" and search_term in book["title"].lower():
            results.append(book)
        elif search_type == "author" and search_term in book["author"].lower():
            results.append(book)
        elif search_type == "genre" and search_term in book["genre"].lower():
            results.append(book)

    return results

def borrow_book(library, isbn, borrower_name):
    """Borrow a book from the library."""
    if isbn in library:
        book = library[isbn]
        if not book["borrowed"]:
            book["borrowed"] = True
            book["borrower"] = borrower_name
            book["times_borrowed"] += 1
            return f"ğŸ“š '{book['title']}' borrowed by {borrower_name}"
        else:
            return f"âŒ '{book['title']}' is already borrowed by {book['borrower']}"
    return "âŒ Book not found"

def return_book(library, isbn):
    """Return a borrowed book to the library."""
    if isbn in library:
        book = library[isbn]
        if book["borrowed"]:
            borrower = book["borrower"]
            book["borrowed"] = False
            book["borrower"] = None
            return f"ğŸ“– '{book['title']}' returned by {borrower}"
        else:
            return f"â„¹ï¸ '{book['title']}' was not borrowed"
    return "âŒ Book not found"

def get_library_stats(library):
    """Generate comprehensive library statistics."""
    if not library:
        return {"total_books": 0}

    total_books = len(library)
    borrowed_books = sum(1 for book in library.values() if book["borrowed"])
    available_books = total_books - borrowed_books

    # Genre distribution
    genres = {}
    for book in library.values():
        genre = book["genre"]
        genres[genre] = genres.get(genre, 0) + 1

    # Popular books (borrowed most)
    popular_books = sorted(
        library.values(), 
        key=lambda x: x["times_borrowed"], 
        reverse=True
    )[:3]

    return {
        "total_books": total_books,
        "available": available_books,
        "borrowed": borrowed_books,
        "borrowing_rate": round((borrowed_books / total_books) * 100, 1) if total_books > 0 else 0,
        "genres": genres,
        "popular_books": popular_books
    }

def print_library_report(library):
    """Print a formatted library report."""
    stats = get_library_stats(library)

    print("ğŸ“š LIBRARY MANAGEMENT REPORT")
    print("=" * 50)
    print(f"Total Books: {stats['total_books']}")
    print(f"Available: {stats['available']} | Borrowed: {stats['borrowed']}")
    print(f"Borrowing Rate: {stats['borrowing_rate']}%")

    if stats["genres"]:
        print("\nğŸ“Š Genre Distribution:")
        for genre, count in sorted(stats["genres"].items()):
            print(f"  {genre}: {count} books")

    if stats["popular_books"]:
        print("\nğŸ† Most Popular Books:")
        for i, book in enumerate(stats["popular_books"], 1):
            if book["times_borrowed"] > 0:
                print(f"  {i}. {book['title']} - borrowed {book['times_borrowed']} times")

# Initialize library and add books
library_catalog = {}

books_to_add = [
    ("1984", "George Orwell", 1949, "978-0-452-28423-4", "Dystopian"),
    ("Pride and Prejudice", "Jane Austen", 1813, "978-0-14-143951-8", "Romance"),
    ("The Hobbit", "J.R.R. Tolkien", 1937, "978-0-547-92822-7", "Fantasy"),
    ("Clean Code", "Robert Martin", 2008, "978-0-13-235088-4", "Technical")
]

print("ğŸ“š Building Library Catalog...")
for title, author, year, isbn, genre in books_to_add:
    book = create_book(title, author, year, isbn, genre)
    if add_book_to_library(library_catalog, book):
        print(f"  âœ… Added: {book['title']} by {book['author']}")

print("\nğŸ” Library Operations:")
print(borrow_book(library_catalog, "978-0-452-28423-4", "Alice"))
print(borrow_book(library_catalog, "978-0-547-92822-7", "Bob"))
print(borrow_book(library_catalog, "978-0-452-28423-4", "Charlie"))  # Should fail

print("\nğŸ“– Search Results for 'Pride':")
search_results = search_books(library_catalog, "Pride", "title")
for book in search_results:
    status = "ğŸ“• Borrowed" if book["borrowed"] else "ğŸ“— Available"
    print(f"  {book['title']} by {book['author']} - {status}")

print("\n" + return_book(library_catalog, "978-0-452-28423-4"))

print()
print_library_report(library_catalog)
